
public class ExOddEvenFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for ( int i=20; i>0; i--) {
			System.out.print("    Even Value of i is " + i  );
			i--;
			System.out.print("      Odd Value of i is "     +i);
			System.out.println();
			
		}

	}


	}


